from AA_string.StringCompare import StringCompare as scfrom AA_string.TextCompare import TextCompare as tcclass CompareFacade:    """A Compare Facade Class """    @staticmethod    def string_distance(source, target):        return sc.string_distance(source, target)    @staticmethod    def string_different(source, target):        return sc.string_different(source, target)    @staticmethod    def text_distance(source, target):        return tc.text_distance(source, target)    @staticmethod    def text_different(source, target):        return tc.text_different(source, target)