from diff import Diff as diffclass TextCompare:    """A utility class to compare text"""    # The data structure representing a diff is an array of tuples:    # [(DIFF_DELETE, "Hello"), (DIFF_INSERT, "Goodbye"), (DIFF_EQUAL, " world.")]    # which means: delete "Hello", add "Goodbye" and keep " world."    DIFF_DELETE = -1    DIFF_INSERT = 1    DIFF_EQUAL = 0    @classmethod    def text_distance(cls, source, target):        """        :param source:        :param target:        :return:        """        pass    @classmethod    def text_different(cls, source, target):        """        :param source: text        :param target: text        :return:        """        sourcewords = cls._text_divided_words(source)        targetWords = cls._text_divided_words(target)        return diff().diff_main(source, target)    @staticmethod    def _text_divided_words(text):        """        :param text:        :return:        """        words = text.split()        return words